//Custom Immutable class
public class Demo4
{
	 private String name;
	 private int age;
	 
	 
	public Demo4(String name, int age) {
		super();
		this.name = name;
		this.age = age;
	}


	public int getAge() {
		// TODO Auto-generated method stub
		return this.age;
	}


	public String getName() {
		// TODO Auto-generated method stub
		return this.name;
	}
	 
	 
}
